import React, { useState } from 'react';
import axios from 'axios';

function NameForm({ onNameSubmit }) {
  const [name, setName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/user', { name });
      onNameSubmit(name); 
      setErrorMessage('');
    } catch (error) {
      console.error('Error submitting name:', error);
      setErrorMessage('Failed to submit name. Please try again.');
    }
  };

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      background: 'url("/background.jpg") no-repeat center center fixed',
      backgroundSize: 'cover',
    }}>
      <div style={{
        display: 'flex',           
        flexDirection: 'column',   
        justifyContent: 'center', 
        alignItems: 'center',    
        backdropFilter: 'blur(10px)',
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '15px',
        padding: '40px',
        maxWidth: '300px',
        width: '100%',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.25)',
      }}>
        <h1 style={{            // Added title
          color: '#f57400',
          marginBottom: '20px',
          fontSize: '24px',
          fontWeight: 'bold'
        }}>
          Exploding Kittens ðŸ˜¸
        </h1>
        <form onSubmit={handleSubmit} style={{
          width: '100%',        // Added to make form take full width
          display: 'flex',      // Added to center form elements
          flexDirection: 'column', // Added to stack form elements vertically
          alignItems: 'center', // Added to center form elements horizontally
        }}>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter Your Name"
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              border: '1px solid #f57400',
              color: '#f57400',
              padding: '10px',
              borderRadius: '5px',
              marginBottom: '15px',
              width: '100%',
              outline: 'none',
              fontSize: '16px',
            }}
          />
          <button
            type="submit"
            style={{
              backgroundColor: '#f57400',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px',
              fontWeight: 'bolder',
              width: '100%',
            }}
          >
            Game on
          </button>
          {errorMessage && <p style={{ color: '#f57400', marginTop: '10px' }}>{errorMessage}</p>}
        </form>
      </div>
    </div>
  );
}

export default NameForm;
