import React, { useState } from 'react';
import axios from 'axios';

const NameForm = ({ onNameSubmit }) => {
  const [name, setName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/user', { name });
      onNameSubmit(name);
      setErrorMessage('');
    } catch (error) {
      console.error('Error submitting name:', error);
      setErrorMessage('Failed to submit name. Please try again.');
    }
  };

  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* Video Background */}
      <video 
        className="absolute top-0 left-0 w-full h-full object-cover -z-10"
        autoPlay 
        loop 
        muted 
        playsInline
      >
        <source src="../assets/" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Overlay to make the form more visible */}
      <div className="absolute top-0 left-0 w-full h-full bg-black/50 -z-5" />

      {/* Form Container */}
      <div className="relative z-10 flex items-center justify-center h-full">
        <form 
          onSubmit={handleSubmit} 
          className="bg-black/30 p-8 rounded-lg backdrop-blur-sm"
        >
          <div className="nameformTitle mb-6">
            <h1 className="text-4xl font-bold text-white">Exploding Kittens ðŸ˜¸</h1>
          </div>
          
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter Your Name"
            className="w-full bg-transparent border-2 border-[#10B881] text-[#10B881] p-2 rounded-md mb-4 outline-none placeholder-[#10B881]/70"
          />
          
          <button
            type="submit"
            className="w-full bg-[#10B881] text-white py-3 px-6 rounded-md cursor-pointer text-lg font-bold hover:bg-[#0ea673] transition-colors duration-200"
          >
            Game on
          </button>
          
          {errorMessage && (
            <p className="mt-4 text-[#10B881]">{errorMessage}</p>
          )}
        </form>
      </div>
    </div>
  );
};

export default NameForm;
